import React, { Component } from 'react'
import { connect } from 'react-redux';
import * as actions from '../redux/actions';

class Canvas extends Component {

  onDragStart = (ev, id) => {
    console.log('dragstart:', id);
    ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  // onDrop = (ev, cat) => {
  //   let id = ev.dataTransfer.getData("id");
  //
  //   let tasks = this.state.tasks.filter((task) => {
  //     if (task.name == id) {
  //       task.category = cat;
  //     }
  //     return task;
  //   });
  //
  //   this.setState({
  //     ...this.state,
  //     tasks
  //   });
  // }

  onDrop = (e) => {
    e.persist()
    const ctx = this.refs.canvas.getContext('2d');
    var img = new Image();
    img.src = e.dataTransfer.getData("id")
    img.onload = function () {
      ctx.drawImage(img,e.clientX-350,e.clientY-120,80,80);
    }
    console.log(this.state)
  }

  render(){
    const canvasStyle = {
      position: 'absolute',
      left: '20%',
      top: '10%',
      backgroundColor: '#EEEEEE',
      border: '1px dotted'}

    return(
      <canvas width={700} height={525} style={canvasStyle} ref='canvas' onDragOver={(e)=>this.onDragOver(e)}
                onDrop={this.onDrop}/>
    )
  }
}

function mapStateToProps(state) {
  return {
    sprites: state.sprite.sprites
  }
}

export default connect(mapStateToProps)(Canvas);
