// import SoundAdapter from '../apis/SoundAdapter';

// export function fetchSoundUrl(){
//   return {type:''}
// }

export function fetchSounds() {
  fetch('http://localhost:3000/api/v1/sounds')
    .then(r => r.json())
    .then(sounds => {
      return setSounds(sounds)
    })
}

export function setSounds(sounds) {
  // debugger
  return {
    type: 'SET_SOUNDS',
    sounds
  }
}

export function playSound() {
  return {
    type: 'PLAY_SOUND',
    payload: {}
  }
}

export function selectSound(selectedSound, uniqueKey) {
  return {
    type: 'SELECT_SOUND',
    payload: {
      selectedSound: selectedSound,
      uniqueKey: uniqueKey
    }
  }
}
