import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux';
import { setSounds } from '../actions/sound'
import { Button, Form, Segment, Message } from 'semantic-ui-react'
import PropTypes from 'prop-types';
import Avatar from '@material-ui/core/Avatar';
// import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';
import { MuiThemeProvider } from '@material-ui/core/styles';
import { colors } from '../App'
import TextField from '@material-ui/core/TextField';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormLabel from '@material-ui/core/FormLabel';

const soundTypes = [{id:1, name: 'guitar'}, {id:2, name: 'drums'}, {id:3, name: 'piano'}]

const styles = theme => ({
  main: {
    width: 'auto',
    display: 'block', // Fix IE 11 issue.
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(400 + theme.spacing.unit * 3 * 2)]: {
      width: 400,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  paper: {
    marginTop: theme.spacing.unit * 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
  },
  avatar: {
    // margin: theme.spacing.unit,
    // backgroundColor: theme.palette.secondary.main,
    backgroundColor: '#915c75'
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing.unit,
  },
  submit: {
    marginTop: theme.spacing.unit * 3,
  },
});

class UploadSound extends Component{
  state = {
    name: "",
    file: null,
    url: null,
    soundType: ""
  };

  // renderTypes = () => {
  //   return soundTypes.map(sound => {
  //     return (<Fragment key={sound.id}><input type="radio" id={sound.name} name="soundType" value={sound.name} onChange={this.handleChange}/><label>{sound.name}</label><br/></Fragment>)
  //   })
  // }

  renderTypes = () => {
    return soundTypes.map(sound => <FormControlLabel kaye={sound.id} id={sound.name} name="soundType" value={sound.name} control={<Radio />} label="Female" />)
  }

  handleSubmit = event => {
    event.preventDefault();
    const data = new FormData();
    data.append("name", this.state.name);
    data.append("file", this.state.file);
    data.append("sound_type", this.state.soundType)
    fetch("http://localhost:3000/api/v1/sounds", {
      method: "POST",
      body: data,
    })
      .then(res => res.json())
      .then(data => {
        this.setState({ url: data[data.length-1].url });
        this.props.setSounds(data)
      });
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleFileUpload = event => {
    console.log(event.nativeEvent, event.target.files, event.target.files[0]);
    this.setState({
      file: event.target.files[0],
    });
  };

  render(){
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          Name: <input
            name="name"
            type="text"
            value={this.state.name}
            onChange={this.handleChange}
          />

          Type: {this.renderTypes()}
          <input type="file" onChange={this.handleFileUpload} />
          <input type="submit" value="Upload" />
        </form>

        {!!this.state.imageURL ? (
          <img src={this.state.imageURL} alt="img" />
        ) : null}

        <MuiThemeProvider theme={colors}>
      <main className={styles.main}>
        <CssBaseline />
        <Paper className={styles.paper}>
          <Avatar className={styles.avatar} >
            <LockIcon color="secondary"/>
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <form className={styles.form} onSubmit={this.handleSubmit}>
            {this.props.failedLogin ? <Message error header={this.props.error} /> : null}
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="name">Name</InputLabel>
              <Input id="name" name="name" autoComplete="name" autoFocus onChange={this.handleChange} value={this.state.name}/>
            </FormControl>
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="name">Name</InputLabel>
              <Input id="name" name="name" autoComplete="name" autoFocus onChange={this.handleChange} value={this.state.name}/>
            </FormControl>
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="soundType">Sound Type</InputLabel>
              <input type="radio" id="test" name="soundType" value="test" onChange={this.handleChange}/><label>"test"</label>
              <input type="radio" id="test" name="soundType" value="test" onChange={this.handleChange}/><label>"test"</label>
              <input type="radio" id="test" name="soundType" value="test" onChange={this.handleChange}/><label>"test"</label>
            </FormControl>

            <FormControl component="fieldset">
          <FormLabel component="legend">Sound Type</FormLabel>
          <RadioGroup
            aria-label="Type"
            name="type"
            value={this.state.value}
            onChange={this.handleChange}
          >
            <FormControlLabel value="female" control={<Radio />} label="Female" />
            <FormControlLabel value="male" control={<Radio />} label="Male" />
            <FormControlLabel value="other" control={<Radio />} label="Other" />
            <FormControlLabel
              value="disabled"
              disabled
              control={<Radio />}
              label="(Disabled option)"
            />
          </RadioGroup>
        </FormControl>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="secondary"
            >
              Upload
            </Button>
          </form>
        </Paper>
      </main>
      </MuiThemeProvider>

      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    sounds: state.sound.sounds
  }
}
//
// function mapDispatchToProps(dispatch){
//   return {
//
//   }
// }
//

export default connect(mapStateToProps, { setSounds })(UploadSound);
// export default UploadSound
