export function setSprites(sprites){
  return {
    type:'SET_SPRITES',
    sprites
  }
}

export function addSprite(sprite){
  return {
    type:'ADD_SPRITE',
    sprite
  }
}

export function selectSprite(uniqueKey){
  return {
    type:'SELECT_SPRITE',
    uniqueKey
  }
}

export function loopSound() {
  return {
    type: 'LOOP_SOUND',
    // payload: {}
  }
}
