import React, { Component } from 'react'
import { connect } from 'react-redux';
import ReactPlayer from 'react-player'
import { PlayerIcon } from 'react-player-controls'

class Player extends Component{
  state = {
    url: ""
  }

  render(){
    console.log('PLAYER PROPS',this.props)
    if(this.props.selectedSprite.sound){
      return(
        <div>
          <ReactPlayer className='player' width={300} height={50} url={this.props.selectedSprite.sound.url} playing controls={true} loop={true}/>
          <PlayerIcon.Play />
          <PlayerIcon.Pause />
          <PlayerIcon.Previous />
        </div>
      )
    }else{
      return <div><ReactPlayer className='player' width={300} height={50} url="" playing controls={true}/>
        <PlayerIcon.Play width={40} height={40}/>
        <PlayerIcon.Pause width={40} height={40}/>
        <PlayerIcon.Previous width={40} height={40}/>
</div>
    }
  }
}

function mapStateToProps(state) {
  return {
    selectedSprite: state.sprite.selectedSprite
  }
}

function mapDispatchToProps(dispatch){
  return {

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Player);
