const SOUND_URL = "http://localhost:3001/sounds";

export default class SoundAdapter {
  static getSound(id) {
    return fetch(`${SOUND_URL}/${id}`)
      .then(res => res.json())
      .then(data => console.log(data.url))
  }
}
