import React, { Component } from 'react'
import { connect } from 'react-redux';
import * as actions from '../actions/actions';
// return(
//   <div style={divStyle} onDragOver={(e)=>this.onDragOver(e)}
//     onDrop={(e)=>{this.onDrop(e, "wip")}}>
//     <h1>Sprite Bar</h1>
//     {this.sprites()}
//     <button onClick={this.props.addSprite}>Add Sprite</button>
//   </div>
// )

class SpriteBar extends Component{

  onDragStart = (ev, id) => {
    ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  sprites = () => {
    const imgStyle={width:'100px', height:'100px'}
    return this.props.sprites.map(sprite => {
      return <Image key={sprite.id} style={imgStyle} onDragStart = {(e) => this.onDragStart(e, sprite.url)}
        draggable src={sprite.url}/>
      })
    }

  render(){
    const divStyle = {
      position: 'absolute',
      width: '150px',
      height: '100vh',
      left: '0',
      top: '10',
      backgroundColor: '#EEEEEE',
      borderRight: '1px dotted'}

    // return(
    //   <div style={divStyle} onDragOver={(e)=>this.onDragOver(e)}
    //     onDrop={(e)=>{this.onDrop(e, "wip")}}>
    //     <h1>Sprite Bar</h1>
    //     {this.sprites()}
    //     <button onClick={this.props.addSprite}>Add Sprite</button>
    //   </div>
    // )
    return(
      <div style={divStyle}>
        <h1>Sprite Bar</h1>
        {this.sprites()}
        <button onClick={this.props.addSprite}>Add Sprite</button>
      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    sprites: state.sprite.sprites
  }
}

export default connect(mapStateToProps, actions)(SpriteBar);
