const initialSpriteState = {
  sprites: []
};


export default function spriteReducer(state = initialSpriteState, action) {
  switch(action.type) {
    case 'CHANGE_SPRITE':
      return { ...state, sprites: [...state.sprites, action.payload] };
    default:
      return state;
  }
}
