    // const playerStyle =  {
    //   position: 'absolute',
    //   bottom:'30px',
    //   left:'40%',
    //   border: '1px dotted'
    // }

    if(this.props.selectedSprite.sound){
      return(
          <ReactPlayer className='player' width={300} height={50} url={this.props.selectedSprite.sound.url} playing controls={true}/>
      )
    }else{
      return null
    }
  }
}

function mapStateToProps(state) {
  return {
    selectedSprite: state.sprite.selectedSprite
  }
}

