import React, { Component } from 'react'
import { connect } from 'react-redux';
// import * as actions from '../redux/actions';
import changeSprite from '../redux/actions'

const image = () =>{
  var img = new Image();
  img.src = e.dataTransfer.getData("id")
  img.onload = function () {
    ctx.drawImage(img,xStart,yStart,80,80);
  }
  this.props.changeSprite(xStart,yStart)
}

class Canvas extends Component {

  onDragStart = (ev) => {
    console.log(this.props.sprites)
    // console.log('dragstart:', id);
    // ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  onDrop = (e) => {
    e.persist()
    let xStart = e.clientX-350
    let yStart = e.clientY-120
    const ctx = this.refs.canvas.getContext('2d');


    // var img = new Image();
    // img.src = e.dataTransfer.getData("id")
    // img.onload = function () {
    //   ctx.drawImage(img,xStart,yStart,80,80);
    // }
    // this.props.changeSprite(xStart,yStart)
  }

  render(){
    console.log(this.props)
    const canvasStyle = {
      position: 'absolute',
      left: '20%',
      top: '10%',
      backgroundColor: '#EEEEEE',
      border: '1px dotted'}

    return(
      <canvas width={700} height={525} style={canvasStyle} ref='canvas' onDragOver={(e)=>this.onDragOver(e)}
        onDragStart = {(e) => this.onDragStart(e)} draggable onDrop={this.onDrop}/>
    )
  }
}

function mapStateToProps(state) {
  return {
    sprites: state.sprite.sprites
  }
}

function mapDispatchToProps(dispatch){
  return{
    changeSprite: (x,y) => {
      dispatch(changeSprite(x,y))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Canvas);
