import { connect } from 'react-redux';
import ReactPlayer from 'react-player'

class Player extends Component{
  state = {
    url: ''
  }

  componentWillMount(){
    console.log('will mount',this.props)
    // fetch(`http://localhost:3000/api/v1/sounds/${this.props.selectedSprite.selectedSound.id}`)
    //   .then(r => r.json())
    //   .then(data => this.setState({
    //     url: data.url
    //   }))
  }

  render(){
    console.log('SOUND PROPS', this.props)
    const playerStyle =  {
      position: 'absolute',
      bottom:'30px',
      left:'40%',
      border: '1px dotted'
    }
    if(this.props.selectedSprite.selectedSound){
      return(
          <ReactPlayer style={playerStyle} width={300} height={50} url={this.state.url} playing controls={true}/>
      )
    }else{
      return null
    }
  }
}

function mapStateToProps(state) {
  return {
    selectedSprite: state.sprite.selectedSprite
  }
}

function mapDispatchToProps(dispatch){
  return {

  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Player);
