import React, { Component } from 'react'
import { connect } from 'react-redux';
import * as actions from '../redux/actions';

const sprites = [
  {name: 'guitar', url:'https://image.flaticon.com/icons/png/128/26/26843.png'},
  {name: 'drums', url:'https://image.flaticon.com/icons/svg/27/27328.svg'}
]

class SpriteBar extends Component{

  onDragStart = (ev, id) => {
    console.log('dragstart:', id);
    ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  sprites = () => {
    return sprites.map(sprite => {
      return <img style="width:500px;height:600px;" onDragStart = {(e) => this.onDragStart(e, sprite.name)}
        draggable src={sprite.url}/>
      })
    }

  render(){
    const divStyle = {position: 'absolute',
      width: '150px',
      height: '100vh',
      left: '0',
      top: '10',
      backgroundColor: '#EEEEEE',
      borderRight: '1px dotted'}

    return(
      <div style={divStyle} onDragOver={(e)=>this.onDragOver(e)}
        onDrop={(e)=>{this.onDrop(e, "wip")}}>
        <h1>Sprite Bar</h1>
        {this.sprites()}
      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    // animalSrc: state.animal.animalSrc,
    // isLoading: state.animal.isLoading,
  }
}

export default connect(mapStateToProps, actions)(SpriteBar);
