import React, { Component } from 'react'
import { connect } from 'react-redux';
import * as actions from '../redux/actions';

class Canvas extends Component {

  onDragStart = (ev, id) => {
    console.log('dragstart:', id);
    ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  // onDrop = (ev, cat) => {
  //   let id = ev.dataTransfer.getData("id");
  //
  //   let tasks = this.state.tasks.filter((task) => {
  //     if (task.name == id) {
  //       task.category = cat;
  //     }
  //     return task;
  //   });
  //
  //   this.setState({
  //     ...this.state,
  //     tasks
  //   });
  // }

  onCanvasDrop = (e) => {
    e.persist()
    console.log('x',e.clientX,'y',e.clientY)
        console.log('pagex',e.pageX,'pagey',e.pageY)
    const ctx = this.refs.canvas.getContext('2d');
    var img = new Image();
    img.onload = function () {
      ctx.drawImage(img, e.clientX, e.clientY, 100, 100);
    }
    img.src = e.dataTransfer.getData("id")
  }

  render(){
    return(
      <canvas onMouseMove={e => console.log(e.clientX,e.clientY)} ref='canvas' width={480} height={360} onDragOver={(e)=>this.onDragOver(e)}
                onDrop={this.onCanvasDrop}/>
    )
  }
}

function mapStateToProps(state) {
  return {
    // animalSrc: state.animal.animalSrc,
    // isLoading: state.animal.isLoading,
  }
}

export default connect(mapStateToProps, actions)(Canvas);
