import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux';
// import * as actions from '../redux/actions';
import changeSprite from '../redux/actions'
import Konva from 'konva';
import { Stage, Layer, Star, Text, Image } from 'react-konva';


class Canvas extends Component {

  // handleDragStart = e => {
  //   e.target.setAttrs({
  //     shadowOffset: {
  //       x: 15,
  //       y: 15
  //     },
  //     scaleX: 1.1,
  //     scaleY: 1.1
  //   });
  // };
  //
  // handleDragEnd = e => {
  //   e.target.to({
  //     duration: 0.5,
  //     easing: Konva.Easings.ElasticEaseOut,
  //     scaleX: 1,
  //     scaleY: 1,
  //     shadowOffsetX: 5,
  //     shadowOffsetY: 5
  //   });
  // };

  onDragStart = (ev) => {
    console.log(this.props.sprites)
    // console.log('dragstart:', id);
    // ev.dataTransfer.setData("id", id);
  }

  onDragOver = (ev) => {
    ev.preventDefault();
  }

  onDrop = (e) => {
    e.persist()
    let xStart = e.clientX-350
    let yStart = e.clientY-120
    // const ctx = this.refs.spriteLayer.getContext('2d');
    // var img = new Image()
    // img.src = e.dataTransfer.getData("id")
    // img.onmouseover =
    // img.onload = function () {
    //   ctx.drawImage(img,xStart,yStart,80,80);
    // }
    // this.props.changeSprite(xStart,yStart)


  }

  render(){
    console.log(this.props)
    const canvasStyle = {
      position: 'absolute',
      left: '20%',
      top: '10%',
      backgroundColor: '#EEEEEE',
      border: '1px dotted'}

    // return(
    //   <Fragment>
    //     <canvas width={700} height={525} style={canvasStyle} ref='canvas' onDragOver={(e)=>this.onDragOver(e)}
    //       onDragStart = {(e) => this.onDragStart(e)} draggable/>
    //     <canvas width={700} height={525} style={canvasStyle} ref='spriteLayer' onDragOver={(e)=>this.onDragOver(e)}
    //       onDragStart = {(e) => this.onDragStart(e)} draggable onDrop={this.onDrop}/>
    //   </Fragment>
    // )
    return(
      <Stage style={canvasStyle} width={700} height={525}>
        <Layer>
        {[...Array(10)].map(i => (
          <Image
            key={i}
            x={Math.random() * window.innerWidth}
            y={Math.random() * window.innerHeight}
            numPoints={5}
            innerRadius={20}
            outerRadius={40}
            fill="#89b717"
            opacity={0.8}
            draggable
            rotation={Math.random() * 180}
            shadowColor="black"
            shadowBlur={10}
            shadowOpacity={0.6}
            onDragStart={this.handleDragStart}
            onDragEnd={this.handleDragEnd}
          />
        ))}
        </Layer>
      </Stage>
    )
  }
}

function mapStateToProps(state) {
  return {
    sprites: state.sprite.sprites
  }
}

function mapDispatchToProps(dispatch){
  return{
    changeSprite: (x,y) => {
      dispatch(changeSprite(x,y))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Canvas);
