import React, { Component } from 'react'
import { connect } from 'react-redux';
// import * as actions from '../actions/actions';
// import Konva from 'konva';
import { Image } from 'react-konva';
// import { selectSprite } from '../actions/sprite'
import * as actions from '../actions/sprite'

class Sprite extends Component{
  state = {
    image: null,
    isDragging: false,
    hover: false,
    uniqueKey: null,
    selected: true
  }

  componentDidMount() {
    const image = new window.Image();
    image.src = this.props.selectedSprite.sprite.url
    image.onload = () => {
      this.setState({
        image: image,
        uniqueKey: this.props.selectedSprite.uniqueKey
      });
    };
  }

  // componentDidUpdate() {
    // const image = new window.Image();
    // image.src = this.props.selectedSprite.sprite.url
    // image.onload = () => {
    //   this.setState({
    //     image: image
    //   });
    // };
  // }

  handleDragStart = () => {
    this.setState({
      isDragging: true
    })
    this.props.selectSprite(this.state.uniqueKey)
  }

  handleDragEnd = () => {
    this.setState({
      isDragging: false
    })
  }

  handleHover = (e) => {
    this.setState({
      hover: true
    })
    // document.body.style.cursor = 'pointer'
  }

  handleHoverOut = (e) => {
    this.setState({
      hover: false
    })
    // document.body.style.cursor = 'default'
  }

  handleSelect = () => {
    this.props.selectSprite(this.state.uniqueKey)
    // this.setState({
    //   selected: true
    // })
  }

  outline = () => {
    if(this.props.selectedSprite.uniqueKey===this.state.uniqueKey){
      this.setState({
        selected: true
      })
    }else{
      this.setState({
        selected: false
      })
    }
  }

  render(){
    console.log('SPRITE PROPS', this.props)
    console.log('SPRITE STATE', this.state)
    return(
      <Image image={this.state.image}
          x={1}
          y={1}
          width={60}
          height={60}
          stroke='#623149'
          strokeWidth={3}
          onClick={this.handleSelect}
          onDragEnd={this.handleDragEnd}
          onDragStart={this.handleDragStart}
          onMouseOver={(e) => this.handleHover(e)}
          onMouseOut={(e) => this.handleHoverOut(e)}
          draggable
          strokeEnabled={this.outline}
          />
    )
  }
}

function mapStateToProps(state) {
  return {
    selectedSprite: state.sprite.selectedSprite,
    canvasSprites: state.sprite.canvasSprites
  }
}

// function mapDispatchToProps(dispatch){
//   return {
//     selectSprite: (selectedSprite) => {
//       dispatch(selectSprite(selectedSprite))
//     }
//   }
// }

export default connect(mapStateToProps, actions)(Sprite);
